CREATE PROCEDURE Proc_employee_GetNewCode ()
SQL SECURITY INVOKER
COMMENT 'author: PNNHai
date: 
description: Procedure để lấy mã nhân viên mới'
BEGIN
  DECLARE currentCode varchar(20);
  DECLARE newCode varchar(20);

  -- Lấy ra mã nhân viên có số cuối cùng lớn nhất
  SELECT
    MAX(CAST(SUBSTRING(EmployeeCode, 4) AS UNSIGNED)) INTO @maxNumber
  FROM employee;

  -- Nếu không có mã nào tồn tại
  IF @maxNumber IS NULL THEN
    SET newCode = 'NV-0001';
  ELSE
    -- Nếu tồn tại mã, thực hiện tăng số và định dạng lại mã mới
    SET @newNumber = @maxNumber + 1;

    -- Định dạng lại số trước khi thêm vào mã
    IF @newNumber < 1000 THEN
      SET newCode = CONCAT('NV-', LPAD(@newNumber, 4, '0'));
    ELSE
      SET newCode = CONCAT('NV-', @newNumber);
    END IF;
  END IF;

  SELECT
    newCode AS NewEmployeeCode;
END
$$

