-- Create procedure `Proc_employee_Filter`
--
CREATE PROCEDURE Proc_employee_Filter (IN i_PageNumber int, IN i_PageSize int, IN i_Where varchar(500), IN i_SortColumn varchar(100), IN i_IsSortDesc bool, IN i_FilterClause varchar(5000), OUT o_TotalRecords int)
SQL SECURITY INVOKER
COMMENT 'author: PNNHai
date: 
desctiption: Procedure lấy danh sách nhân viên kết hợp lọc thông tin, tìm kiếm và phân trang
i_PageNumber: Số trang hiện tại
i_Where: Điều kiện tìm kiếm (Theo mã và tên)
i_SortColumn: Cột muốn thực hiện sắp xếp
i_IsSortDesc: (true: DESC | false: ASC)
i_FilterString: Chuỗi điều kiện lọc dữ liệu '
BEGIN
  -- Khai báo hiến để lấy offset là bản ghi bắt đầu lấy
  DECLARE recordOffset int;

  -- Nếu điều kiện có thì thực hiện tìm kiếm theo Code hoặc Name
  IF (i_Where IS NOT NULL) THEN
    SET @whereClause = CONCAT('(EmployeeCode LIKE \'%', i_Where, '%\' OR EmployeeName LIKE \'%', i_Where, '%\')');
  -- Nếu không có điều kiện tìm kiếm -> 1 = 1 -> luôn đúng
  ELSE
    SET @whereClause = '1=1';
  END IF;

  -- Nếu khối lệnh filter có tồn tại -> nối thêm điều kiện where = where + khối điều kiện filter
  IF (i_FilterClause IS NOT NULL) THEN
    SET @whereClause = CONCAT(@whereClause, i_FilterClause);
  END IF;

  -- Nếu điều kiện sắp xếp theo cột không có -> mặc định sắp xếp theo giảm dần theo ModifiedDate
  IF IFNULL(i_SortColumn, '') = '' THEN
    SET i_SortColumn = 'e.ModifiedDate DESC';
  -- Trường hợp có điều kiện sắp xếp
  ELSE
    -- Nếu trường cần sắp xếp là Name -> Cắt chuỗi lấy chữ cái cuối cùng là tên
    IF (i_SortColumn = 'EmployeeName') THEN
      SET i_SortColumn = CONCAT('SUBSTRING_INDEX(', i_SortColumn, ', \' \', -1)');
    END IF;

    -- Sắp xếp theo kiểu unicode
    SET i_SortColumn = CONCAT(i_SortColumn, ' COLLATE utf8mb4_unicode_ci');

    -- Có sắp xếp giảm dần hay không ??? -> Nếu có -> DESC
    IF (i_isSortDesc = TRUE) THEN
      SET i_SortColumn = CONCAT(i_SortColumn, ' DESC');
    END IF;
  END IF;

  SET @filterQuery = 'SELECT  e.EmployeeId,           e.EmployeeCode,       e.EmployeeName, 
                              e.IsCustomer,           e.IsProvider,         e.DateOfBirth , 
                              e.Gender,               e.DepartmentId,       d.DepartmentName,
                              e.PositionName,         e.IdentityNumber,     e.IdentityIssuedDate, 
                              e.IdentityIssuedPlace,  e.Address,            e.PhoneNumber,        
                              e.LandlineNumber,       e.Email,              e.BankNumber,         
                              e.BankName,             e.BankBranch 
                      FROM employee e LEFT JOIN department d 
                      ON e.DepartmentId = d.DepartmentId';

  -- Nếu pageNumber <= 0 hoặc pageSize < 0 => Lấy hết bản ghi
  IF i_PageNumber <= 0
    OR i_PageSize < 0 THEN
    SET @filterQuery = CONCAT(@filterQuery, ' WHERE ', @whereClause, ' ORDER BY ', i_SortColumn);

  -- Lấy bản ghi có phân trang
  ELSE
    -- Bản ghi bắt đầu lấy / trang (offset ở trong limit bắt đầu lấy từ 0 -> pageNumber - 1)
    SET recordOffset = (i_PageNumber - 1) * i_PageSize;
    SET @filterQuery = CONCAT(@filterQuery, ' WHERE ', @whereClause, ' ORDER BY ', i_SortColumn, ' LIMIT ', recordOffset, ',', i_PageSize);
  END IF;

  -- THỰC THI CÂU SQL
  PREPARE filterQuery FROM @filterQuery;
  EXECUTE filterQuery;
  DEALLOCATE PREPARE filterQuery;

  -- CÂU LỆNH ĐỂ TRẢ VỀ SỐ LƯỢNG BẢN GHI VÀO OUT PARAMETTER
  SET @totalCountQuery = CONCAT('SELECT COUNT(*) INTO @totalRecord FROM employee e WHERE ', @whereClause);
  PREPARE totalCount FROM @totalCountQuery;
  EXECUTE totalCount;
  DEALLOCATE PREPARE totalCount;

  SET o_TotalRecords = @totalRecord;
END
$$

